import { Component, Input} from '@angular/core';
import { Skill } from 'src/app/DTO/skill.model';
import { SkillService } from 'src/app/services/skill.service';

@Component({
  selector: 'app-list-skills',
  templateUrl: './list-skills.component.html',
  styleUrls: ['./list-skills.component.scss']
})
export class ListSkillsComponent {
  @Input() title:string = "Liste de mes compétences";

  listCard : Skill[] = [];

  constructor(private SkillService:SkillService){

    this.listCard = this.SkillService.getAllFromDB();

    this.listCard.forEach(element => {
      console.log(element.name);
    })

    // // WITH API
    // this.SkillService.getAll().subscribe(resultat => {
    //   this.listCard = resultat;
    // })
  }
}
