import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { ResourceService } from '../../../Services/resource/resource.service';
import { Resource } from '../../../DTO/resource';

@Component({
  selector: 'app-resource-create',
  templateUrl: './resource-create.component.html',
  styleUrls: ['./resource-create.component.css']
})
export class ResourceCreateComponent {
  createForm: FormGroup;
  errorMessage: string;

  constructor(
    private formBuilder: FormBuilder,
    private resourceService: ResourceService,
    private router: Router
  ) {
    this.createForm = this.formBuilder.group({
      title: ['', Validators.required],
      description: ['', Validators.required],
      content: ['', Validators.required],
      imagePath: ['', Validators.required],
      createdBy: ['', Validators.required],
      createdAt: ['', Validators.required]
    });
  }

  createResource(): void {
    if (this.createForm.valid) {
      const resource: Resource = this.createForm.value;
      this.resourceService.createResource(resource);
      this.router.navigate(['/resources']);
    } else {
      this.errorMessage = 'Veuillez remplir tous les champs du formulaire.';
    }
  }
}
<!-- Nous utilisons le formulaire réactif d'Angular en associant le formulaire avec le groupe de contrôles createForm défini dans le composant TypeScript.
Chaque champ du formulaire est lié à un contrôle dans le groupe de contrôles via l'attribut formControlName.
Le bouton "Create" est désactivé tant que le formulaire est invalide (c'est-à-dire tant que tous les champs requis ne sont pas remplis).
Lorsque le formulaire est soumis ((ngSubmit)="createResource()"), la méthode createResource() du composant TypeScript est appelée. -->


<h2>Create Resource</h2>
<div *ngIf="errorMessage" class="error-message">
  {{ errorMessage }}
</div>
<form [formGroup]="createForm" (ngSubmit)="post()">
  <div class="form-group">
    <label for="title">Title</label>
    <input type="text" id="title" formControlName="title" class="form-control" required>
  </div>
  <div class="form-group">
    <label for="description">Description</label>
    <textarea id="description" formControlName="description" class="form-control" required></textarea>
  </div>
  <div class="form-group">
    <label for="type">Type</label>
    <select id="type" formControlName="type" class="form-control" required>
      <option value="">Select type</option>
      <option value="photo">Photo</option>
      <option value="message">Message</option>
      <option value="article">Article</option>
    </select>
  </div>
  <div class="form-group">
    <label for="file">File</label>
    <input type="file" id="file" formControlName="file" class="form-control" required>
  </div>
  <button type="submit" [disabled]="createForm.invalid">Create</button>
</form>
