import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ResourceService } from '../../services/resource.service';
import { Resource } from '../../models/resource';

@Component({
  selector: 'app-resource-edit',
  templateUrl: './resource-edit.component.html',
  styleUrls: ['./resource-edit.component.css']
})
export class ResourceEditComponent {
  editForm: FormGroup;
  errorMessage: string;
  resource: Resource;

  constructor(
    private formBuilder: FormBuilder,
    private resourceService: ResourceService,
    private router: Router,
    private route: ActivatedRoute
  ) {
    const resourceId = this.route.snapshot.params['id'];
    this.resource = this.resourceService.getResourceById(resourceId);

    if (!this.resource) {
      this.router.navigate(['/resources']);
    }

    this.editForm = this.formBuilder.group({
      title: [this.resource.title, Validators.required],
      description: [this.resource.description, Validators.required],
      content: [this.resource.content, Validators.required],
      imagePath: [this.resource.imagePath, Validators.required],
      createdBy: [this.resource.createdBy, Validators.required],
      createdAt: [this.resource.createdAt, Validators.required]
    });
  }

  updateResource(): void {
    if (this.editForm.valid) {
      const updatedResource: Resource = { ...this.resource, ...this.editForm.value };
      this.resourceService.updateResource(this.resource.id, updatedResource);
      this.router.navigate(['/resources']);
    } else {
      this.errorMessage = 'Veuillez remplir tous les champs du formulaire.';
    }
  }
}
