import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
// import { Observable } from 'rxjs';
import { Skill } from '../DTO/skill.model';

@Injectable({
  providedIn: 'root'
})
export class SkillService {

  // If not API implemented
  database = {
    skill: [
      {
        id:1,
        name:".NET",
      }
    ],
  }

  /**
   * If API implemented, make routes for each endoints of application
  **/

  public endpoint = "https:/localhost/swagger";
  public getEndPoint = this.endpoint+"/GetAll";

  // Constructeur
  constructor(private httpClient:HttpClient) { }

  // WITH API

  get(id:number):Observable<Skill>{
      return this.httpClient.get<Skill>(this.getEndPoint+id)
  }

  getAll():Observable<Skill[]>{
    return this.httpClient.get<Skill[]>(this.getEndPoint);
  }
  post(skill){
    return this.httpClient.post<Skill>(this.getEndPoint, skill);
  }

  update(skill){
    return this.httpClient.put<Skill>(this.getEndPoint, skill);
  }
  delete(id:number){
    return this.httpClient.delete<Skill[]>(this.getEndPoint+id);
  }

