import { Component } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { Category } from 'chemin/vers/le/fichier/category.model';
import { CategoryService } from 'chemin/vers/le/fichier/category.service';

@Component({
  selector: 'app-category-create',
  templateUrl: './category-create.component.html',
  styleUrls: ['./category-create.component.scss']
})
export class CategoryCreateComponent {
  categoryForm: FormGroup;

  constructor(private formBuilder: FormBuilder, private categoryService: CategoryService) {
    this.categoryForm = this.formBuilder.group({
      name: ['', Validators.required],
      description: ['', Validators.required]
    });
  }

  onSubmit(): void {
    if (this.categoryForm.invalid) {
      return;
    }

    const category: Category = {
      name: this.categoryForm.value.name,
      description: this.categoryForm.value.description
    };

    this.categoryService.createCategory(category).subscribe(createdCategory => {
      // Gérer la catégorie créée ici (par exemple, afficher un message de succès)
    });
  }
}
