import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-contact',
  templateUrl: './contact.component.html',
  styleUrls: ['./contact.component.scss']
})
export class ContactComponent implements OnInit {
  contactForm: FormGroup;

  constructor(private formBuilder: FormBuilder) {}

  ngOnInit(): void {
    this.initContactForm();
  }

  initContactForm(): void {
    this.contactForm = this.formBuilder.group({
      name: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      message: ['', Validators.required]
    });
  }

  submitContactForm(): void {
    if (this.contactForm.valid) {
      // Envoyer le formulaire de contact (implémentation de la logique d'envoi ici)
      console.log('Formulaire de contact soumis !');
      console.log(this.contactForm.value);

      // Réinitialiser le formulaire après l'envoi
      this.contactForm.reset();
    }
  }
}
<div class="container">
  <div class="row justify-content-center">
    <div class="col-md-6">
      <div class="card">
        <div class="card-body">
          <h2 class="card-title text-center">Contactez-nous</h2>
          <form [formGroup]="contactForm" (ngSubmit)="submitContactForm()">
            <div class="form-group">
              <label for="name">Nom</label>
              <input type="text" class="form-control" id="name" formControlName="name" placeholder="Votre nom">
              <div *ngIf="contactForm.get('name').invalid && contactForm.get('name').touched" class="invalid-feedback">
                Veuillez entrer votre nom.
              </div>
            </div>
            <div class="form-group">
              <label for="email">Email</label>
              <input type="email" class="form-control" id="email" formControlName="email" placeholder="Votre email">
              <div *ngIf="contactForm.get('email').invalid && contactForm.get('email').touched" class="invalid-feedback">
                Veuillez entrer une adresse email valide.
              </div>
            </div>
            <div class="form-group">
              <label for="message">Message</label>
              <textarea class="form-control" id="message" formControlName="message" placeholder="Votre message"></textarea>
              <div *ngIf="contactForm.get('message').invalid && contactForm.get('message').touched" class="invalid-feedback">
                Veuillez entrer un message.
              </div>
            </div>
            <button type="submit" class="btn btn-primary" [disabled]="contactForm.invalid">Envoyer</button>
          </form>
        </div>
      </div>
    </div>
  </div>
</div>
