import { Injectable } from '@angular/core';
import { Observable, of } from 'rxjs';
import { Category } from '../../DTO/category';

@Injectable({
  providedIn: 'root'
})
export class CategoryService {
  private categories: Category[] = [];

  constructor() {}

  getCategories(): Observable<Category[]> {
    // Ici, vous pouvez implémenter la logique pour récupérer les catégories depuis le backend
    // Par exemple, effectuer une requête HTTP GET pour obtenir les catégories depuis une API

    // Pour cet exemple, nous renvoyons simplement les catégories enregistrées localement
    return of(this.categories);
  }

  createCategory(category: Category): Observable<Category> {
    // Ici, vous pouvez implémenter la logique pour créer une nouvelle catégorie dans le backend
    // Par exemple, effectuer une requête HTTP POST pour enregistrer la catégorie dans une API

    // Pour cet exemple, nous ajoutons simplement la catégorie à la liste locale des catégories
    this.categories.push(category);
    return of(category);
  }
}
