import { Component } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { CategoryService } from '../../../Services/category/category.service';
import { Category } from '../../../DTO/category';

@Component({
  selector: 'app-category-create',
  templateUrl: './category-create.component.html',
  styleUrls: ['./category-create.component.scss']
})
export class CategoryCreateComponent {
  categoryForm: FormGroup;

  constructor(private formBuilder: FormBuilder, private categoryService: CategoryService) {
    this.categoryForm = this.formBuilder.group({
      title: ['', Validators.required],
      description: ['', Validators.required]
    });
  }

  onSubmit(): void {
    if (this.categoryForm.invalid) {
      return;
    }

    const category: Category = {
      title: this.categoryForm.value.title,
      description: this.categoryForm.value.description
    };

    this.categoryService.createCategory(category).subscribe(createdCategory => {
      // Gérer la catégorie créée ici (par exemple, afficher un message de succès)
    });
  }
}
<div class="container">
  <h1>Creer une categorie</h1>
  <form (ngSubmit)="createCategory()">
    <div class="form-group">
      <label for="title">Title</label>
      <input type="text" class="form-control" id="title" [(ngModel)]="category.title" name="title" required>
    </div>
    <div class="form-group">
      <label for="description">Description</label>
      <textarea class="form-control" id="description" [(ngModel)]="category.description" name="description" required></textarea>
    </div>
    <div class="form-group">
      <label for="content">Content</label>
      <textarea class="form-control" id="content" [(ngModel)]="category.content" name="content" required></textarea>
    </div>
    <div class="form-group">
      <label for="image_path">Image Path</label>
      <input type="text" class="form-control" id="image_path" [(ngModel)]="category.image_path" name="image_path">
    </div>
    <button type="submit" class="btn btn-primary">Create</button>
  </form>
</div>
