import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Resource } from '../../models/resource';
import { ResourceService } from '../../services/resource.service';

@Component({
  selector: 'app-resource-share',
  templateUrl: './resource-share.component.html',
  styleUrls: ['./resource-share.component.css']
})
export class ResourceShareComponent implements OnInit {
  selectedResource: Resource;
  recipientEmail: string;
  errorMessage: string;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private resourceService: ResourceService
  ) { }

  ngOnInit(): void {
    const resourceId = this.route.snapshot.paramMap.get('id');
    this.loadResource(resourceId);
  }

  loadResource(id: string): void {
    this.resourceService.getResourceById(id).subscribe(
      (resource: Resource) => {
        this.selectedResource = resource;
      },
      (error: any) => {
        this.errorMessage = 'Failed to load resource.';
      }
    );
  }

  share(): void {
    // Implement your sharing logic here
    // You can use this.selectedResource and this.recipientEmail to send the resource to the recipient
    // Once sharing is done, you can navigate to another page or perform any other actions
  }
}
