import { Component, OnInit } from '@angular/core';
import { Resource } from '../../../DTO/resource';
import { ResourceService } from '../../../Services/resource/resource.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-resource-create',
  templateUrl: './resource-create.component.html',
  styleUrls: ['./resource-create.component.scss']
})
export class ResourceCreateComponent implements OnInit {
  resource: Resource = {
    id: '',
    title: 'toto',
    description: 'toto',
    content: 'toto',
    image_path: 'toto',
    createdBy: 'toto',
    createdAt: 'toto',
    comment: 'toto',
    type: 'toto',
  };

  constructor(
    private resourceService: ResourceService,
    private router: Router
  ) { }

  ngOnInit(): void {
  }

  createResource(): void {
    this.resourceService.createResource(this.resource).subscribe(() => {
      this.router.navigate(['/resources']);
    });
  }
}
<br>
<div class="container">
  <h1>Créer une nouvelle ressource</h1>
  <form (ngSubmit)="createResource()">
    <div class="form-group">
      <label for="title">Titre de la ressource</label>
      <input type="text" class="form-control" id="title" [(ngModel)]="resource.title" name="title" required>
    </div>
    <div class="form-group">
      <label for="description">Description</label>
      <textarea class="form-control" id="description" [(ngModel)]="resource.description" name="description" required></textarea>
    </div>
    <div class="form-group">
      <label for="content">Contenu</label>
      <textarea class="form-control" id="content" [(ngModel)]="resource.content" name="content" ></textarea>
    </div>
    <div class="form-group">
      <label for="type">Veuillez choisir le type de ressource créée</label>
      <select class="form-control" id="type" [(ngModel)]="resource.type" name="type" required>
        <option value="image">Articles / publications </option>
        <option value="videos">Evenements</option>
        <option value="article">Images ou vidéos</option>
        <option value="type1">Documentation et outil</option>
        <option value="type1">Jeux ludiques</option>
      </select>
    </div>
    <div class="form-group">
      <label for="image_path">Intégrer votre fichier</label>
      <input type="text" class="form-control" id="image_path" [(ngModel)]="resource.image_path" name="image_path">
    </div>
    <div class="form-group">
      <label for="createdBy">Créer par</label>
      <input type="text" class="form-control" id="createdBy" [(ngModel)]="resource.createdBy" name="createdBy" required>
    </div>
    <div class="form-group">
      <label for="createdAt">Créer le </label>
      <input type="text" class="form-control" id="createdAt" [(ngModel)]="resource.createdAt" name="createdAt" required>
    </div>
    <br>
    <div clas="buttonSave">
      <button type="submit" class="btn btn-primary">Créer la ressource</button>
    </div>
  </form>
</div>
