import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ResourceService } from '../../../Services/resource/resource.service';
import { Resource } from '../../../DTO/resource';
import { Comment } from '../../../DTO/comment';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-resource-details',
  templateUrl: './resource-details.component.html',
  styleUrls: ['./resource-details.component.css']
})
export class ResourceDetailsComponent implements OnInit {
  resource$: Observable<Resource>;

  constructor(
    private route: ActivatedRoute,
    private resourceService: ResourceService
  ) { }

  ngOnInit(): void {
    this.loadResource();
  }

  loadResource(): void {
    const resourceId = this.route.snapshot.paramMap.get('id');
    this.resource$ = this.resourceService.getResourceById(resourceId);
  }

  addComment(commentText: string): void {
    const resourceId = this.route.snapshot.paramMap.get('id');
    const newComment: Comment = {
      id: uuidv4(),
      text: commentText
    };
    this.resourceService.addComment(resourceId, newComment).subscribe(
      (resource) => {
        if (resource) {
          // Le commentaire a été ajouté avec succès à la ressource
          console.log('Commentaire ajouté :', newComment);
          // Recharger la ressource pour afficher les commentaires mis à jour
          this.loadResource();
        } else {
          // La ressource n'a pas été trouvée, traitement d'erreur
          console.error('Ressource non trouvée');
        }
      }
    );
  }
}
