/**
 * importé les modules Component, OnInit, FormGroup, FormBuilder, Validators et Router depuis le module @angular/core et le module @angular/forms ;
 * importé les services AuthService et ResourceService depuis leurs fichiers respectifs ;
 * créé la classe ResourceCreateComponent qui implémente l'interface OnInit ;
 * initialisé les propriétés createForm et errorMessage dans le constructeur ;
 * défini la méthode ngOnInit qui crée le formulaire de création de ressource avec le module FormBuilder ;
 * défini la méthode createResource qui crée une ressource en appelant la méthode createResource du service ResourceService et redirige vers la liste des ressources en cas de succès ou affiche un message d'erreur en cas d'échec.
 */
import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';

import { AuthService } from '../../../Services/auth/auth.service';
import { ResourceService } from '../../../Services/resource/resource.service';
import { Resource } from '../../../DTO/resource';

@Component({
  selector: 'app-resource-create',
  templateUrl: './resource-create.component.html',
  styleUrls: ['./resource-create.component.css']
})
export class ResourceCreateComponent implements OnInit {
  createForm: FormGroup;
  errorMessage: string;

  constructor(
    private fb: FormBuilder,
    private authService: AuthService,
    private resourceService: ResourceService,
    private router: Router
  ) { }

  ngOnInit(): void {
    this.createForm = this.fb.group({
      title: ['', Validators.required],
      description: ['', Validators.required],
      content: ['', Validators.required],
      image_path: ['', Validators.required],
    });
  }

  post(): void {
    const newResource: Resource = {
      id: '', // Ajoutez une valeur d'ID appropriée ici
      title: this.createForm.value.title,
      description: this.createForm.value.description,
      content: this.createForm.value.content,
      image_path: '', // Ajoutez le chemin d'image approprié ici
      createdBy: '', // Ajoutez l'identifiant de l'utilisateur approprié ici
      createdAt: new Date() // Utilisez la date actuelle ou une autre valeur appropriée ici
    };

    this.resourceService.post(newResource).subscribe(
      () => {
        // Ressource créée avec succès
        // Vous pouvez effectuer les actions souhaitées, comme afficher un message de réussite ou naviguer vers une autre page
      },
      (error: any) => {
        this.errorMessage = 'Échec de la création de la ressource.';
      }
    );
  }
}
<!-- Nous utilisons le formulaire réactif d'Angular en associant le formulaire avec le groupe de contrôles createForm défini dans le composant TypeScript.
Chaque champ du formulaire est lié à un contrôle dans le groupe de contrôles via l'attribut formControlName.
Le bouton "Create" est désactivé tant que le formulaire est invalide (c'est-à-dire tant que tous les champs requis ne sont pas remplis).
Lorsque le formulaire est soumis ((ngSubmit)="createResource()"), la méthode createResource() du composant TypeScript est appelée. -->


<h2>Create Resource</h2>
<div *ngIf="errorMessage" class="error-message">
  {{ errorMessage }}
</div>
<form [formGroup]="createForm" (ngSubmit)="post()">
  <div class="form-group">
    <label for="title">Title</label>
    <input type="text" id="title" formControlName="title" class="form-control" required>
  </div>
  <div class="form-group">
    <label for="description">Description</label>
    <textarea id="description" formControlName="description" class="form-control" required></textarea>
  </div>
  <div class="form-group">
    <label for="type">Type</label>
    <select id="type" formControlName="type" class="form-control" required>
      <option value="">Select type</option>
      <option value="photo">Photo</option>
      <option value="message">Message</option>
      <option value="article">Article</option>
    </select>
  </div>
  <div class="form-group">
    <label for="file">File</label>
    <input type="file" id="file" formControlName="file" class="form-control" required>
  </div>
  <button type="submit" [disabled]="createForm.invalid">Create</button>
</form>
