import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { Resource } from '../../../DTO/resource';
import { ResourceService } from '../../../Services/resource/resource.service';

@Component({
  selector: 'app-resource-edit',
  templateUrl: './resource-edit.component.html',
  styleUrls: ['./resource-edit.component.css']
})
export class ResourceEditComponent implements OnInit {
  editForm: FormGroup;
  errorMessage: string;
  resourceId: string;

  constructor(
    private formBuilder: FormBuilder,
    private route: ActivatedRoute,
    private router: Router,
    private resourceService: ResourceService
  ) { }

  ngOnInit(): void {
    this.resourceId = this.route.snapshot.paramMap.get('id');
    this.buildForm();
    this.loadResource();
  }

  buildForm(): void {
    this.editForm = this.formBuilder.group({
      title: ['', Validators.required],
      description: ['', Validators.required],
      content: ['', Validators.required],
      image_path: ['', Validators.required],
    });
  }

  loadResource(): void {
    this.resourceService.getResource(this.resourceId).subscribe(
      (resource: Resource) => {
        this.editForm.patchValue(resource);
      },
      (error: any) => {
        this.errorMessage = 'Failed to load resource.';
      }
    );
  }

  updateResource(): void {
    if (this.editForm.invalid) {
      return;
    }

    const updatedResource: Resource = {
      id: this.resourceId,
      ...this.editForm.value
    };

    this.resourceService.updateResource(updatedResource).subscribe(
      () => {
        this.router.navigate(['/resources']);
      },
      (error: any) => {
        this.errorMessage = 'Failed to update resource.';
      }
    );
  }
}
<h2>Edit Resource</h2>
<div *ngIf="errorMessage" class="error-message">
  {{ errorMessage }}
</div>
<form [formGroup]="editForm" (ngSubmit)="updateResource()">
  <div class="form-group">
    <label for="title">Title</label>
    <input type="text" id="title" formControlName="title" class="form-control" required>
  </div>
  <div class="form-group">
    <label for="description">Description</label>
    <textarea id="description" formControlName="description" class="form-control" required></textarea>
  </div>
  <div class="form-group">
    <label for="type">Type</label>
    <select id="type" formControlName="type" class="form-control" required>
      <option value="">Select type</option>
      <option value="photo">Photo</option>
      <option value="message">Message</option>
      <option value="article">Article</option>
    </select>
  </div>
  <button type="submit" [disabled]="editForm.invalid">Save Changes</button>
</form>
