import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';

import { Resource } from '../../DTO/resource';
// import { AuthService } from '../auth/auth.service';

@Injectable({
  providedIn: 'root'
})

export class ResourceService {
  private apiUrl = 'http://localhost:3000/api/resources';

  constructor(
    private http: HttpClient,
    private authService: AuthService
  ) { }

  getResources(): Observable<Resource[]> {
    const headers = new HttpHeaders({
      'Authorization': `Bearer ${this.authService.getToken()}`
    });

    return this.http.get<Resource[]>(this.apiUrl, { headers });
  }

  getResource(id: string): Observable<Resource> {
    const headers = new HttpHeaders({
      'Authorization': `Bearer ${this.authService.getToken()}`
    });

    return this.http.get<Resource>(`${this.apiUrl}/${id}`, { headers });
  }

  createResource(resource: Resource): Observable<Resource> {
    const headers = new HttpHeaders({
      'Authorization': `Bearer ${this.authService.getToken()}`
    });

    return this.http.post<Resource>(this.apiUrl, resource, { headers });
  }

  updateResource(id: string, resource: Resource): Observable<Resource> {
    const headers = new HttpHeaders({
      'Authorization': `Bearer ${this.authService.getToken()}`
    });

    return this.http.put<Resource>(`${this.apiUrl}/${id}`, resource, { headers });
  }

  deleteResource(id: string): Observable<Resource> {
    const headers = new HttpHeaders({
      'Authorization': `Bearer ${this.authService.getToken()}`
    });

    return this.http.delete<Resource>(`${this.apiUrl}/${id}`, { headers });
  }
}
