  providedIn: 'root'
})
export class SkillService {

  // If not API implemented
  database = {
    skill: [
      {
        id:1,
        name:".NET",
        text:"text .NET"
      },
      {
        id:2,
        name:"Angular",
        text:"text Angular"
      },
      {
        id:3,
        name:"DDD",
        text:"text DDD"
      }
    ],
