  // }
  // delete(id:number){
  //   return this.httpClient.delete<Skill[]>(this.getEndPoint+id);
  // }

  // // WITHOUT API
  getFromDB(id){
    return this.database.skill.find(x => x.id == id);
  }

  getAllFromDB(){
    return this.database.skill;
  }

  postFromDB(skill){
     this.httpClient.post<Skill>(this.getEndPoint, skill);
  }

  deleteFromDB(id){
    this.httpClient.delete<Skill>(this.getEndPoint+id);
  }

  updateFomDB(skill){
    let query = this.database.skill.findIndex(x => x.id == skill.id);

  }

}
