import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ResourceService } from '../../../Services/resource/resource.service';
import { Resource } from '../../../DTO/resource';

@Component({
  selector: 'app-resource-share',
  templateUrl: './resource-share.component.html',
  styleUrls: ['./resource-share.component.scss']
})
export class ResourceShareComponent {
  resource: Resource;

  constructor(private route: ActivatedRoute, private resourceService: ResourceService) {
    const resourceId = this.route.snapshot.params['id'];
    this.resource = this.resourceService.getResourceById(resourceId);
  }
}
