import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-contact',
  templateUrl: './contact.component.html',
  styleUrls: ['./contact.component.scss']
})
export class ContactComponent implements OnInit {
  contactForm: FormGroup;

  constructor(private formBuilder: FormBuilder) {}

  ngOnInit(): void {
    this.initContactForm();
  }

  initContactForm(): void {
    this.contactForm = this.formBuilder.group({
      name: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      message: ['', Validators.required]
    });
  }

  submitContactForm(): void {
    if (this.contactForm.valid) {
      // Envoyer le formulaire de contact (implémentation de la logique d'envoi ici)
      console.log('Formulaire de contact soumis !');
      console.log(this.contactForm.value);

      // Réinitialiser le formulaire après l'envoi
      this.contactForm.reset();
    }
  }
}
<h2>Contact</h2>

<form [formGroup]="contactForm" (ngSubmit)="submitContactForm()">
  <div>
    <label for="name">Nom</label>
    <input type="text" id="name" formControlName="name" required>
    <div *ngIf="contactForm.get('name').invalid && (contactForm.get('name').dirty || contactForm.get('name').touched)">
      <div *ngIf="contactForm.get('name').hasError('required')">Le nom est requis.</div>
    </div>
  </div>

  <div>
    <label for="email">Adresse e-mail</label>
    <input type="email" id="email" formControlName="email" required>
    <div *ngIf="contactForm.get('email').invalid && (contactForm.get('email').dirty || contactForm.get('email').touched)">
      <div *ngIf="contactForm.get('email').hasError('required')">L'adresse e-mail est requise.</div>
      <div *ngIf="contactForm.get('email').hasError('email')">L'adresse e-mail n'est pas valide.</div>
    </div>
  </div>

  <div>
    <label for="message">Message</label>
    <textarea id="message" formControlName="message" required></textarea>
    <div *ngIf="contactForm.get('message').invalid && (contactForm.get('message').dirty || contactForm.get('message').touched)">
      <div *ngIf="contactForm.get('message').hasError('required')">Le message est requis.</div>
    </div>
  </div>

  <button type="submit" [disabled]="contactForm.invalid">Envoyer</button>
</form>
